import { StatusBar } from "expo-status-bar";
import { StyleSheet, Text, View } from "react-native";

export default function App() {
  return (
    <View style={styles.container}>
      <View style={styles.top}>
        <Text style={[styles.textTop, styles.textTopLeft]}>Cam on phong</Text>
        <Text>Phong that tuyet</Text>
        <StatusBar style="auto" />
      </View>
      <View style={styles.center}>
        <Text style={styles.textCenter}>Cam on phong</Text>
      </View>
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: "#fff",
  },
  top: {
    flex: 1,
    backgroundColor: "red",
  },
  textTop: {
    textAlign: "center",
  },
  center: {
    flex: 1,
    backgroundColor: "blue",
  },
  textCenter: {
    textAlign: "center",
  },
});
